#!/usr/bin/env node
"use strict";

var _require = require('fs'),
    readFileSync = _require.readFileSync;

var _require2 = require('path'),
    join = _require2.join;

require('./check-npm-version');

var updateDb = require('./');

var ROOT = __dirname;

function getPackage() {
  return JSON.parse(readFileSync(join(ROOT, 'package.json')));
}

var args = process.argv.slice(2);
var USAGE = 'Usage:\n  npx update-browserslist-db\n';

function isArg(arg) {
  return args.some(function (i) {
    return i === arg;
  });
}

function error(msg) {
  process.stderr.write('update-browserslist-db: ' + msg + '\n');
  process.exit(1);
}

if (isArg('--help') || isArg('-h')) {
  process.stdout.write(getPackage().description + '.\n\n' + USAGE + '\n');
} else if (isArg('--version') || isArg('-v')) {
  process.stdout.write('browserslist-lint ' + getPackage().version + '\n');
} else {
  try {
    updateDb();
  } catch (e) {
    if (e.name === 'BrowserslistUpdateError') {
      error(e.message);
    } else {
      throw e;
    }
  }
}